import React from 'react';
import {useState, useEffect} from 'react';
import axios from 'axios';
import styled from 'styled-components'

const NewsCard = styled.div`
        flex: 0 0 30%;
        margin-right: 3rem;
        background: #222C38;
`;

const onWheel = e => {
    e.preventDefault();
    const container = document.getElementById("container");
    const containerScrollPosition = document.getElementById("container").scrollLeft;
    container.scrollTo({
      top: 0,
      left: containerScrollPosition + e.deltaY,
      behaviour: "smooth"
    });
  };

function News() {
    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [items, setItems] = useState([]);

    const [appState, setAppState] = useState({
        loading: null,
        repos: 0,
      });

          useEffect(() => {
            setAppState({ loading: true });
            const apiUrl = 'http://api.mediastack.com/v1/news?access_key=c0c5d36f2b0915afb0b0623c65134349&countries=de&keywords=corona';
            axios.get(apiUrl).then((repos) => {
              const allRepos = repos.data;
              setItems( allRepos.data );
              console.log(allRepos);
            });
          }, [setAppState]);


          console.log(items);


        return( <div className="news">
            <div className="headline">
                <h1>Corona</h1>
                <h2>News</h2>
            </div>
            <div className="newsCards" onWheel={onWheel} id="container">
            {items.length < 1 ? "fetching" : 
                items.map((news, key) =>
                    <NewsCard className="card">
                        <img src="./assets/coronavirus.png" />
                        <h4>{news.title}</h4>
                        <p>{news.description}</p>
                        <a href={news.url}>weiter lesen</a>
                    </NewsCard>
                  )
            }
            </div>
         </div>
     )

}

export default News;
