import React from 'react';
import {useState, useEffect} from 'react';
import axios from 'axios';
import styled from 'styled-components'

const NewsCard = styled.div`
`;

function News() {
    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [items, setItems] = useState([]);

    const [appState, setAppState] = useState({
        loading: null,
        repos: 0,
      });

          useEffect(() => {
            setAppState({ loading: true });
            const apiUrl = 'http://api.mediastack.com/v1/news?access_key=c0c5d36f2b0915afb0b0623c65134349&countries=de&keywords=corona';
            axios.get(apiUrl).then((repos) => {
              const allRepos = repos.data;
              setItems( allRepos.data );
              console.log(allRepos);
            });
          }, [setAppState]);


          console.log(items);



        return( <div className="news">
            {items.length < 1 ? "fetching" : 
                items.map((news, key) =>
                    <NewsCard>
                        <h4>{news.title}</h4>
                        <p>{news.description}</p>
                        <button href={news.url}>weiter lesen</button>
                    </NewsCard>
                  )
            }
         </div>
     )

}

export default News;
